class VDOM_flextext(VDOM_object):

	def render(self, contents=""):

#------------------------------------------------------------------------------------------------------------------------------

		# Visible

		if self.visible == "1":
			invisible = ""
		else:
			invisible = "display:none;"

		# class

		if self.classname == "":
			classname = ""
		else:
			classname = " class='%s' " % ( self.classname )
		the_value = self.value

		# ID

		id = 'o_' + (self.id).replace('-', '_')

		# debug

		if VDOM_CONFIG_1["DEBUG"] == "1":
			debug_info = u"objtype='flextext' objname='%s' ver='%s'" % (self.name, self.type.version)
		else:
			debug_info = u""

		# oveerflow

		if self.overflow == "1":
			overflow = u"overflow: hidden;"
		elif self.overflow == "2":
			overflow = u"overflow: scroll;"
		elif self.overflow == "3":
			overflow = u"overflow: visible;"
		elif self.overflow == "0":
			overflow = u"overflow: auto;"
		else:
			overflow = u""

		# Display

		if self.displaying == "1": 
			displaying = u" display: block; "
		elif self.displaying == "2":
			displaying = u" display: inline; "
		elif self.displaying == "3":
			displaying = u" display: inline-block; "
		else:
			displaying = u""

		if self.visible == "0":
			displaying = u" display: none; " 

		# Float

		if self.float == "0": 
			float = u""
		elif self.float == "1":
			float = u" float: left; "
		elif self.float == "2":
			float = u" float: right; "
		elif self.float == "3":
			float = u" float: both; "
		else:
			float = u""

		# Layers and Order

		style_zindex = u"z-index:%s;" % self.zindex if int(self.zindex) <> 0 else u""

		# Coordinates
		# Left
		if self.superleft == "auto":
			left_coord = u" left: auto; "
		elif self.superleft == "":
			left_coord = u""
		else:
			left_coord = u""" left: {left}; """\
					.format(left = self.superleft)
		# Top
		if self.supertop == "auto":
			top_coord = u" top: auto; "
		elif self.supertop == "":
			top_coord = u""
		else:
			top_coord = u""" top: {top}; """\
					.format(top = self.supertop)
		# Right
		if self.right == "auto":
			right_coord = u" right: auto; "
		elif self.right == "":
			right_coord = u""
		else:
			right_coord = u""" right: {right}; """\
					.format(right = self.right)
		# Bottom
		if self.bottom == "auto":
			bottom_coord = u" bottom: auto; "
		elif self.bottom == "":
			bottom_coord = u""
		else:
			bottom_coord = u""" bottom: {bottom}; """\
					.format(bottom = self.bottom)

		if self.positioning1 == "2":
			if self.superleft != "": 			
				left_coord = u""" left: {left}; """\
					.format(left = self.superleft)		
			else:			
				left_coord = u""
			if self.supertop != "": 			
				top_coord = u""" top: {top}; """\
					.format(top = self.supertop)		
			else:			
				top_coord = u""		
		elif self.positioning1 == "0":
			left_coord = u""
			top_coord = u""


		coordinates = u""" {top} {left} {right} {bottom} """\
					.format(top = top_coord, left = left_coord, bottom = bottom_coord, right = right_coord)

		# width and height

		if self.superwidth != "":
			superwidth = u""" width:{width};"""\
				.format(width = self.superwidth)
		else:
			superwidth = u""	
		if self.superheight != "":
			superheight = u""" height:{height};"""\
				.format(height = self.superheight)
		else:
			superheight = u""


		# Positioning mode

		if self.positioning1 == "0": 
			positioning = u""
		elif self.positioning1 == "1":
			positioning = u" position: absolute; "
		elif self.positioning1 == "2":
			positioning = u" position: relative; "
		elif self.positioning1 == "3":
			positioning = u" position: fixed; "
		else:
			positioning = u""


		# Margings

		if self.margins == "":
			margins = u""
		else:
			margins = u""" margin: {margin}; """\
					.format(margin = self.margins)
		# Paddings

		if self.paddings == "":
			paddings = u""
		else:
			paddings = u""" padding: {padding}; """\
					.format(padding = self.paddings)

		# Compound style

		style = u"""{display} {zind} {positioning} {width} {height} {coordinatess} {overflow} {margin} {padding} {floatt}"""\
				.format(display = displaying, 
					zind = style_zindex,
					positioning = positioning, 
					margin = margins, 
					padding = paddings,
					width = superwidth,
					height = superheight,
					coordinatess = coordinates,
					floatt = float,
					overflow = overflow
					)

#------------------------------------------------------------------------------------------------------------------------------
		result = u"<div %s id=\"%s\" style=\"%s\" %s>%s</div>" % (debug_info, id, style, classname, the_value)

		return VDOM_object.render(self, contents=result)

	def wysiwyg(self,  contents=""):

		the_value = self.value

		empty_value = \
			u"""<svg>
				<text fill="#cccccc" x="7" y="13" >Text</text>
			</svg>
			""" if the_value == "" else ""


		color = """color="%s" """ % self.color if self.color != "" else ""

		cdata = "<![CDATA"+"["+the_value+"]"+"]>"

		result = \
			u"""<container id="{id}" visible="{visible}" zindex="{zindex}" hierarchy="{hierarchy}" order="{order}" top="{top}" left="{left}" width="{width}" height="{height}">
					<svg>
					<rect x="0" y="0" width="{width1}" height="{height1}" fill="" style="stroke-width:1; stroke:rgb(9,120,240)"/>
					</svg>
					{empty_value}
					<htmltext top="{textTop}" left="{textLeft}" width="{width}" height="{height}" {color} editable="{value}">{cdata}</htmltext>
					{contents}
				</container>""".format(
					id = self.id,
					visible = self.visible,
					zindex = self.zindex,
					hierarchy = self.hierarchy,
					order = self.order,
					top = self.top,
					left = self.left,
					width = self.width,
					height = self.height,
					width1 = self.width,
					height1 = self.height,
					empty_value = empty_value,
					textTop = 0,
					textLeft = 0,
					color = color,
					value = "value",
					cdata = cdata,
					contents = contents
				)

		return VDOM_object.wysiwyg(self, contents=result)


def on_update(object, attributes):
	if object.parent and object.parent.type.class_name == "VDOM_tabview" and object.attributes["lockposition"] == "1" and int(object.attributes["top"]) != 20 and int(object.attributes["left"]) != 1:
		attributes.update(left="1", top="20")

	modifications = {}

	# Left
	if "left" in attributes:
		if 'px' in object.attributes.get('superleft', '').lower():
			newleft = str(attributes["left"])+"px"
			modifications.update(superleft=newleft)

	if "superleft" in attributes:
		superleft = attributes["superleft"]
		if 'px' in superleft.lower():
			modifications.update(left = int(superleft[0:superleft.find("px")]))

	# top
	if "top" in attributes:
		if 'px' in object.attributes.get('supertop', '').lower():
			newtop = str(attributes["top"])+"px"
			modifications.update(supertop=newtop)

	if "supertop" in attributes:
		supertop = attributes["supertop"]
		if 'px' in supertop.lower():
			modifications.update(top = int(supertop[0:supertop.find("px")]))

	# width
	if "width" in attributes:
		if 'px' in object.attributes.get('superwidth', '').lower():
			newwidth = str(attributes["width"])+"px"
			modifications.update(superwidth=newwidth)

	if "superwidth" in attributes:
		superwidth = attributes["superwidth"]
		if 'px' in superwidth.lower():
			modifications.update(width = int(superwidth[0:superwidth.find("px")]))

	# height
	if "height" in attributes:
		if 'px' in object.attributes.get('superheight', '').lower():
			newheight = str(attributes["height"])+"px"
			modifications.update(superheight=newheight)

	if "superheight" in attributes:
		superheight = attributes["superheight"]
		if 'px' in superheight.lower():
			modifications.update(width = int(superheight[0:superheight.find("px")]))





	attributes.update(modifications)
	return ""
