class VDOM_formrender(VDOM_object):

    def render(self, contents=""):
        display = u"display: none;" if self.visible == "0" else u""
        woid = (self.id).replace('-', '_')
        language = {
			'en-US': 'a9f8d2bc-1a80-bc8e-11cd-1e4a7b049588',
			'fr-FR': 'a34e5e27-27a4-2210-b689-1e4a8b79c487',
			'ru-RU': 'a86bbdc0-012d-4942-f010-1e4a9759bba4'
		}
        current_language = language.get(self.language, 'a9f8d2bc-1a80-bc8e-11cd-1e4a7b049588')

        id = u"o_" + woid
        style = u"z-index:{zind};position:{pos};top:{top}px;left:{left}px;width:{wid}px;height:{hei}px;" \
                    .format(zind = self.zindex, pos = self.position, 
                                top = self.top, left = self.left, wid = self.width, hei = self.height)

        classname = u"""class="%s" """ % self.classname if self.classname else u""

        if VDOM_CONFIG_1["DEBUG"] == "1":
            debug_info = u"objtype='formRender' objname='%s' ver='%s'" % (self.name, self.type.version)
        else:
            debug_info = u""

        result = \
                    u"""
                    <div {debug_info} id="{id}" style="display:block;margin:0; {display} {style}" name="{name}" style="overflow:visible" {classname}></div>
       <script>
              const {id} = {{}};
              {id}.formData = {contents};
              jQuery(function($) {{
                $('#{id}').formRender({{fields: $.formRenderModules.fields, templates: $.formRenderModules.templates ,dataType: '{datatype}',formData: {id}.formData, i18n: {{
                  locale: '{language}',
                  location: '/',
                  extension: '.lang',
                }}}});

                //fix rerender(for example onchange VDOM attrs)
                $('body').off("change", "#{id} input, #{id} select");

                $('body').on("change", "#{id} input, #{id} select", e => {{
                    const $input = $(e.currentTarget);
                    const itemName = $input.attr('name');
                    const itemValue = $input.val();
                    execEventBinded('{id}'.slice(2), 'inputonchange', {{itemName, itemValue}});
                  }}
                );
              }});
       </script>""".format(
                               debug_info = debug_info,
                               id = id,
                               woid= woid,
                               display = display,
                               style = style,
                               name = self.name, 
                               contents = self.data or "[]" if self.datatype=='json' else "'<form-template></form-template>'",
                               classname = classname,
                               datatype=self.datatype,
                               language=current_language)


        return VDOM_object.render(self, contents=result)

    def wysiwyg(self, contents=""):
        from scripting.legacy.wysiwyg import get_empty_wysiwyg_value

        image_id = "54e7f8ab-64f1-b113-029e-1703a76c6fa4"
        result = get_empty_wysiwyg_value(self, image_id)

        return VDOM_object.wysiwyg(self, contents=result)

