class ListField(Field):
separator = ","
def __init__(self, separator=None, *args, **kwargs): if separator is not None: self.separator = separator
super(ListField, self).__init__(*args, **kwargs)
def clone_base(self, **kwargs): return super(ListField, self).clone_base( separator=self.separator, **kwargs )
def xapian_value(self, value): return self.separator.join(map(self.coerce, value))
def python_value(self, value): return value.split(self.separator)
class ContactsListField(ListField):
def contacts_list_to_string(self, contacts): result = [] for contact in contacts: result.extend(map(self.coerce, contact))
return sorted(result)
def coerce(self, value): return smart_str(value).replace("@", "_")\ .replace(".", "_")
def xapian_term(self, value): return self.contacts_list_to_string(value)
def xapian_value(self, value): return self.separator.join(self.contacts_list_to_string(value))
def xapian_query_value(self, value): return self.coerce(value)
class ContactField(ContactsListField):
def contact_to_string(self, contact): return sorted(map(self.coerce, contact))
def xapian_term(self, value): return self.contact_to_string(value)
def xapian_value(self, value): return self.separator.join(self.contact_to_string(value))
class Message(Document):
class Meta: database = database
