def parse_msg_date(msg): """ Get Date from E-mail """ date_str = msg.get_decoded_header("Date") date = None
if date_str: date = email.utils.parsedate_tz(date_str)
if not date: for hname, hvalue in msg.items(): if "Received" in hname: date_str = hvalue.rsplit(";",1)[1] date = email.utils.parsedate_tz(date_str)
if date: break
if date: date = datetime.fromtimestamp(email.utils.mktime_tz(date))
return date or ''
