import json import poplib import smtplib import subprocess
from inspect import getdoc
from promail_orm import Connectivity from promail_tasks import tasks
def run_tests(tests):
block_sep = "\n\n" + "=" * 30 + "\n\n" test_sep = "\n\n" + "-" * 50 + "\n\n" output = [''] stat = []
for test_set in tests:
test_output = [] test_stat = [] params = test_set[1]
for name, test in test_set[0]:
out, err = test(params)
out = getdoc(test).format(**params) + "\n" + out out += "\n--== Test result: {} ==--".format("failed" if err else "passed")
test_output.append(out) test_stat.append([name, err]) if err: break
output.append(test_sep.join(test_output)) stat.append(test_stat)
return { "output": block_sep.join(output), "stat": stat }
def test_ping(server): """--== Test: ping {host} ==--"""
addr = server["host"] output =[]
ping = subprocess.Popen(["ping", "-c", '4', addr], stdout=subprocess.PIPE, stderr=subprocess.PIPE) out, err = ping.communicate()
output.append(out or err) error = ping.returncode
return "\n".join(output), bool(error)
def test_pop3(server): """--== Test: POP3 connection to {host}:{port} (secure - {secure}) ==--"""
auth = server.get('auth', None) addr = server["host"] port = int(server["port"]) secure = Connectivity.SECURITY_E(server["secure"]) output = [] error = False
try: conn = (poplib.POP3_SSL if secure == Connectivity.SECURITY_E.SSL else \ poplib.POP3)(addr, port)
output.append("Connecting to server...") output.append("Sent 'getwelcome'...") output.append("Response: {}".format(conn.getwelcome()))
if auth: output.append("Sent 'user'...") output.append("Response: {}".format(conn.user(auth['login']))) output.append("Sent 'pass_'...") output.append("Response: {}".format(conn.pass_(auth['passwd'])))
except Exception as ex: output.append("Error: {}".format(str(ex))) error = True
return "\n".join(output), error
def test_smtp(server): """--== Test: SMTP connection to {host}:{port} (secure - {secure}) ==--"""
auth = server.get('auth', None) addr = server["host"] port = int(server["port"]) secure = Connectivity.SECURITY_E(server["secure"]) output = [] error = False
try: conn = (smtplib.SMTP_SSL if secure == Connectivity.SECURITY_E.SSL else \ smtplib.SMTP)(addr, port)
output.append("Connecting to server...") output.append("Sent 'ehlo'...") output.append("Response: {}".format(str(conn.ehlo())))
if secure == Connectivity.SECURITY_E.STARTTLS: output.append("Starting TLS ...") output.append("Response: {}".format(str(conn.starttls())))
if auth: output.append("Sent 'login'...") output.append("Response: {}".format(conn.login(auth['login'], auth['passwd'])))
except Exception as ex: output.append("Error: {}".format(str(ex))) error = True
return "\n".join(output), error
@tasks.background_ui(attempts=2) def test_server_connection(server): """ Test connection to server """ tests = [ [ [ ["pop3_ping", test_ping], ["pop3_conn", test_pop3], ], server["in"] ], [ [ ["smtp_ping", test_ping], ["smtp_conn", test_smtp], ], server["out"] ], ]
return json.dumps(run_tests(tests))
