	def version( self ):
		return self.find( 'version' )

	@version.setter
	def version( self, value ):
		self['version'] = value

	@property
	def author( self ):
		return self.find( 'author' )

	@author.setter
	def author( self, value ):
		self['author'] = value

	@property
	def protected( self ):
		return self.find( 'protected' )

	@protected.setter
	def protected( self, value ):
		self['protected'] = value

