login_page_url 			= "/login"
home_page_url  			= "/"
proadmin_attention_url 	= "/proadmin_attention"
license_url 			= "/license"



vdom_container = None
def redirect( url ):
	if vdom_container:
		vdom_container.action( "goTo", [ url ] )
	else:
		response.redirect( url )



import ProAdmin
def authenticated( method ):
	def wrapper( *args, **kwargs ):
		if ProAdmin.current_user() is None:
			redirect( login_page_url )
		else:
			return method()
	return wrapper


def administrator( method ):
	def wrapper( *args, **kwargs ):
		current_user = ProAdmin.current_user()
		if bool( ProAdmin.application().rules( subject = current_user, access='a' ) ):
			return method()
		else:
			redirect( home_page_url )
	return wrapper


def local_scheme( method ):
	def wrapper( *args, **kwargs ):
		if ProAdmin.scheme().is_remote():
			redirect( proadmin_attention_url )
		else:
			return method()
	return wrapper

def license_confirmed( method ):
	def wrapper( *args, **kwargs ):
		return method()
	return wrapper


from engine.exceptions import RenderTermination
def error_handler( method ):
	def wrapper( *args, **kwargs ):
		try:
			return method()
		except RenderTermination:
			raise
		except Exception, ex:
			session[ "error" ] = unicode(ex)
			redirect( home_page_url )
	return wrapper

def active_directory_scheme( method ):
	def wrapper( *args, **kwargs ):
		 if ProAdmin.scheme().type == "active_directory":
		 	return method()
	return wrapper

"""
"""

import ProAdmin


class ProSuiteUser(object):

    def __init__(self, proadmin_user):
        self._user = proadmin_user

    def __hash__(self):
        return hash(self.guid)

    def __eq__(self, other):
        return self.guid == other.guid

    @property
    def user(self):
        return self._user

    @property
    def guid(self):
        return self.user.guid

    @property
    def name(self):
        return self.user.name

    @property
    def notification_email(self):
        return self.user.notification_email

    def is_admin(self):
        return bool(ProAdmin.application().rules(subject=self.user, access="a"))

    @classmethod
    def current_user(cls):
        """
        Return logged in user
        """
        proadmin_user = ProAdmin.current_user()
        return cls(proadmin_user) if proadmin_user else None

    @classmethod
    def get_by_guid(cls, guid):
        """
        Find user by GUID
        """
        proadmin_user = ProAdmin.application().get_users(guid=guid)
        return cls(proadmin_user[0]) if proadmin_user else None

from utils.threads import VDOM_thread


appId = application.id


class compilerTimeTrigger( VDOM_thread ):
	# default wait timeout
	DEFAULT_TIMEOUT = 10.0

	def __init__( self ):
		VDOM_thread.__init__( self )


	def work( self ):
		import VEE_core
#		VEE_core.engine.engine_logger.debug("Compiler thread work...")
		try:
			application.set_app_id( appId )
			self.save_thread()
			return VEE_core.engine.do_compile()
		except Exception as e:
#			from vdom_trace import Trace

			VEE_core.engine.engine_logger.exception("@@@@@@@@@Error while vscript compilation." )
#			VEE_core.engine.engine_logger.info( Trace.exception_trace() )

#			try:
#				VEE_core.engine.engine_logger..info( str( e ) )
#			except: pass
			try:
				return self.DEFAULT_TIMEOUT
			except:
				self.stop()


	def save_thread( self ):
		try:
			import VEE_core
			VEE_core.compiler_thread = self
		except:
			pass

""" """
import threading import time
from prosuite_settings import settings
class ProSuiteBaseThread(threading.Thread): """ ProSuite base thread with logging """
def __init__(self, *args, **kwargs): super(ProSuiteBaseThread, self).__init__(*args, **kwargs) self._stop_event = threading.Event() self._logger = None
def setup_logger(self, parent_logger, logger=None): """ Setup logger """ self._logger = logger or parent_logger.getChild(self.name)
@property def logger(self): return self._logger
def kill_existing_instance(self): """ Kill thread with same name as if exists """ self.logger.debug("Search threads starting with name '%s'...", self.name)
# enumerate over existing threads for thread in threading.enumerate():
# check name and # thread is instance of VDOM_thread or not if not thread.name.startswith(self.name): continue
args = ( thread.getName(), thread.ident, thread.__class__.__name__ ) self.logger.debug( "Thread %s:%s (%s class) was found! Trying stop it...", *args ) try: thread.stop()
except Exception as ex: self.logger.debug( "Can't stop thread %s:%s (%s class)!", *args )
def stop(self): """ Set 'stop' flag to true """ self._stop_event.set()
def is_running(self): """ Check is thread running or not """ return not self._stop_event.is_set()
def set_app_id(self): """ Set application ID """ application.set_app_id(settings.info["id"])
def prepare(self): """ Prepare env """ self.set_app_id()
def cleanup(self): """ Cleanup env """ pass
def main(self): """ Main function """ pass
def run(self): try: self.prepare() self.main()
except Exception as ex: self.logger.exception("Exception in thread.run...")
finally: self.cleanup()
class ProSuiteDaemon(ProSuiteBaseThread): """ Basic ProSuite daemon class """
DEFAULT_QUANTUM = 1.0 DEFAULT_TIMEOUT = 30.0 DEFAULT_NAME = "Daemons.ProSuite"
def __init__(self, *args, **kwargs): super(ProSuiteDaemon, self).__init__(*args, **kwargs) self.name = settings.info["id"] + "." + self.DEFAULT_NAME
def main(self): """ Mail loop """ while self.is_running(): self.set_app_id() self.sleep(self.work())
self.logger.info("Stop event was set... Exiting...")
def sleep(self, seconds=0): """ """ remainder = seconds or self.DEFAULT_TIMEOUT
while remainder > 0 and self.is_running(): value = min(self.DEFAULT_QUANTUM, remainder) time.sleep(value) remainder -= value
def work(self): """ Main function of the thread. Executed every DEFAULT_TIMEOUT seconds """ try: self.save_thread() self.process() except Exception as ex: self.logger.exception("Exception in 'work'")
finally: return self.DEFAULT_TIMEOUT
def save_thread(self): """ Save thread to module variable """ pass
def process(self): """ Write your code here to run in thread """ pass
class ProSuiteThreadPool(object):
__slots__ = ( "_pool", "_size", "_name", "_pm_name", "_pm_cls", "_logger", "_counter", )
def __init__(self, name, size, pm_name="worker", pm_cls=ProSuiteBaseThread): self._logger = None self._name = name self._pm_name = "%s.%s" % (self._name, pm_name) self._pm_cls = pm_cls self._pool = self.get_pool() self._size = size self._counter = 0
def setup_logger(self, parent_logger): """ Setup logger """ self._logger = parent_logger.getChild(self.name)
@property def logger(self): return self._logger
def get_pool(self): return []
@property def name(self): return self._name
@property def worker_name(self): return self._pm_name
@property def worker_class(self): return self._pm_cls
@property def size(self): return self._size
def inc_counter(self): if self._counter > self._size ** 4: self._counter = 0
self._counter += 1 return self._counter
def is_fully_loaded(self): """ Check is pool full or not """ return bool(self.size and len(self._pool) >= self.size)
def start_worker(self, *args, **kwargs): if self.is_fully_loaded(): return None
name = "%s-%d" % (self.worker_name, self.inc_counter()) worker = self.worker_class(name=name, *args, **kwargs) self.add_pool_member(worker) worker.start()
return worker
# def create_worker(self, *args, **kwargs): # """ # Create new worker and add it to pool # """ # if self.is_fully_loaded(): # return None # # name = "%s-%d" % (self.worker_name, len(self._pool)) # worker = self.worker_class(name=name, *args, **kwargs) # # return worker # # def add_worker(self, worker): # """ # Add worker to pool # """ # if self.is_fully_loaded(): # return False # # self.add_pool_member(worker) # return True
def add_pool_member(self, member): """ Add pool member @{member} to pool """ self._pool.append(member)
def get_member(self, pool_member): """ Find pool member in pool @{pool_member} can be dictionary key in case when @{pool} is dictionary """ return pool_member
def remove_member(self, pool_member): """ Remove pool member from pool """ self._pool.remove(pool_member)
def pool_copy(self): return self._pool[:]
def check_members(self): """ Check is pool member alive or not """ if not self._pool: return
for member in self.pool_copy():
thread = self.get_member(member) if not thread.is_alive(): self.remove_member(member)
args = ( thread.getName(), thread.ident, thread.__class__.__name__ ) self.logger.debug( "Pool member %s (%s, %s) is dead. Remove it from pool", *args )

