def update_text_color(self): """ Calculate @_text_color based on @_color i.e. if @_color is 'black' - @_text_color will be 'white' """ rgb = struct.unpack('BBB',self.color[1:].decode('hex')) hls = colorsys.rgb_to_hls(rgb[0],rgb[1],rgb[2]) self._text_color = "#000" if hls[1] >= 175 else "#FFF"
